const text = items[0].json.text; // Assuming the text content is stored in the 'text' property of the first item
const lines = text.split('\n'); // Split the text into lines for easier processing

// Initialize object to store extracted data
const data = {
  candidateName: '',
  referenceNumber: '',
  clientName: '',
  dateEnding: '',
  formattedDateEnding: '', // New attribute for the transformed date
  location: '',
  projectName: '', // Assume this is previously added or will be added
  timeEntries: [],
  signedBy: '',
  signedAt: '',
  ipAddresses: [],
  documentId: '',
  verificationDate: ''
};

// Improved extraction logic
data.candidateName = lines[6].match(/Candidate:\s(.+?)\sReference:/)?.[1] || "Not available";
data.referenceNumber = lines[6].match(/Reference:\s(\d+)/)?.[1] || "Not available";

// Extract location and dateEnding with improved logic
const clientAndDateLine = lines.find(line => line.includes("Client:"));
if (clientAndDateLine) {
    data.dateEnding = clientAndDateLine.match(/Date Ending:\s([^]*)/)?.[1] || "Not specified";
    data.clientName = clientAndDateLine.match(/Client:\s(.+?)\sDate Ending/)?.[1] || "Not available";
}

const locationLine = lines.find(line => line.includes("Location:"));
if (locationLine) {
    data.location = locationLine.split('Location: ')[1].trim();
}

// Convert dateEnding to DD/MM/YYYY format
if (data.dateEnding !== "Not specified") {
    const dateParts = data.dateEnding.split(' '); // Split "21 Dec 2024" into parts
    const months = { Jan: '01', Feb: '02', Mar: '03', Apr: '04', May: '05', Jun: '06', Jul: '07', Aug: '08', Sep: '09', Oct: '10', Nov: '11', Dec: '12' };
    const day = dateParts[0].padStart(2, '0'); // Ensure day is two digits
    const month = months[dateParts[1]]; // Convert month name to month number
    const year = dateParts[2];
    data.formattedDateEnding = `${day}/${month}/${year}`; // Format to DD/MM/YYYY
}

// Extract signature and verification details
data.signedBy = text.match(/Signed by:\s(.+?)\sSigned at:/)?.[1] || "Not available";
data.signedAt = text.match(/Signed at:\s(.+?),/)?.[1] || "Not specified";
data.ipAddresses = text.match(/IP Address:\s([\d.,\s]+)/)?.[1].split(',').map(ip => ip.trim()) || [];
data.documentId = text.match(/Document ID:\s([\d\s]+)/)?.[1].replace(/\s+/g, '').trim() || "Not available";
data.verificationDate = text.match(/Date:\s(\d{2} \w+ \d{4})/)?.[1] || "Not specified";

// Extract time entries and flatten them
const entryRegex = /^(Sunday|Monday|Tuesday|Wednesday|Thursday|Friday|Saturday)\s(\d{1,2}\s\w+\s\d{4})\s(\d+\.\d+)\s(\d+\.\d+)$/;
const flattenedEntries = [];
lines.forEach(line => {
  const match = line.match(entryRegex);
  if (match) {
    flattenedEntries.push({
      json: {
        candidateName: data.candidateName,
        referenceNumber: data.referenceNumber,
        clientName: data.clientName,
        dateEnding: data.dateEnding,
        formattedDateEnding: data.formattedDateEnding, // Include new formatted date attribute
        location: data.location,
        day: match[1],
        date: match[2],
        hours: parseFloat(match[3]),
        days: parseFloat(match[4]),
        signedBy: data.signedBy,
        signedAt: data.signedAt,
        ipAddresses: data.ipAddresses,
        documentId: data.documentId,
        verificationDate: data.verificationDate
      }
    });
  }
});

return flattenedEntries;
